mapper.insert:
INSERT INTO TB_META_ACCOUNT (
ACCOUNT_CD, USER_SEQ, ACCOUNT_CTG, ACCOUNT_NM, ISUE_AGY, ACCOUNT_HRDR, USE_YN, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{ACCOUNT_CD}, #{USER_SEQ}, #{ACCOUNT_CTG}, #{ACCOUNT_NM}, #{ISUE_AGY}, #{ACCOUNT_HRDR}, 'Y', #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.update:
UPDATE TB_META_ACCOUNT
SET ACCOUNT_NM = #{ACCOUNT_NM}, ACCOUNT_CTG = #{ACCOUNT_CTG}, ISUE_AGY = #{ISUE_AGY}, ACCOUNT_HRDR = #{ACCOUNT_HRDR}, UPDATE_ID = #{UPDATE_ID}, UPDATE_DATE = NOW()
WHERE ACCOUNT_CD = #{ACCOUNT_CD}

UPDATE TB_META_ACCOUNT
SET USE_YN = 'N', UPDATE_ID = #{UPDATE_ID}, UPDATE_DATE = NOW()
WHERE ACCOUNT_CD = #{ACCOUNT_CD}

mapper.insert:
INSERT INTO TB_TRAN_UNEARNED(
UED_SEQ, USER_SEQ, UED_DATE, UED_INCM, UED_SOURCE, UED_CTG, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{UED_SEQ}, #{USER_SEQ}, #{UED_DATE}, #{UED_INCM}, #{UED_SOURCE}, #{UED_CTG}, #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.select:
SELECT UED_SEQ, USER_SEQ, DATE_FORMAT(UED_DATE, '%Y-%m-%d') AS UED_DATE, FORMAT(UED_INCM, 0) AS UED_INCM, UED_SOURCE, UED_CTG, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
FROM TB_TRAN_UNEARNED
WHERE UED_SEQ = #{UED_SEQ}

mapper.update:
 
 	 UPDATE TB_TRAN_UNEARNED
 	 
		SET UED_DATE   = #{UED_DATE},
		    UED_INCM   = #{UED_INCM},
		    UED_SOURCE = #{UED_SOURCE},
		    UED_CTG    = #{UED_CTG}
		
		WHERE UED_SEQ  = #{UED_SEQ}
