mapper.insert:
INSERT INTO TB_META_ACCOUNT (
ACCOUNT_CD, USER_SEQ, ACCOUNT_CTG, ACCOUNT_NM, ISUE_AGY, ACCOUNT_HRDR, USE_YN, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{ACCOUNT_CD}, #{USER_SEQ}, #{ACCOUNT_CTG}, #{ACCOUNT_NM}, #{ISUE_AGY}, #{ACCOUNT_HRDR}, 'Y', #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.update:
UPDATE TB_META_ACCOUNT
SET ACCOUNT_NM = #{ACCOUNT_NM}, ACCOUNT_CTG = #{ACCOUNT_CTG}, ISUE_AGY = #{ISUE_AGY}, ACCOUNT_HRDR = #{ACCOUNT_HRDR}, UPDATE_ID = #{UPDATE_ID}, UPDATE_DATE = NOW()
WHERE ACCOUNT_CD = #{ACCOUNT_CD}

UPDATE TB_META_ACCOUNT
SET USE_YN = 'N', UPDATE_ID = #{UPDATE_ID}, UPDATE_DATE = NOW()
WHERE ACCOUNT_CD = #{ACCOUNT_CD}

mapper.insert:
INSERT INTO TB_TRAN_UNEARNED(
UED_SEQ, USER_SEQ, UED_DATE, UED_INCM, UED_SOURCE, UED_CTG, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{UED_SEQ}, #{USER_SEQ}, #{UED_DATE}, #{UED_INCM}, #{UED_SOURCE}, #{UED_CTG}, #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.select:
SELECT UED_SEQ, USER_SEQ, DATE_FORMAT(UED_DATE, '%Y-%m-%d') AS UED_DATE, FORMAT(UED_INCM, 0) AS UED_INCM, UED_SOURCE, UED_CTG, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
FROM TB_TRAN_UNEARNED
WHERE UED_SEQ = #{UED_SEQ}

mapper.update:
 
 	 UPDATE TB_TRAN_UNEARNED
 	 
		SET UED_DATE   = #{UED_DATE},
		    UED_INCM   = #{UED_INCM},
		    UED_SOURCE = #{UED_SOURCE},
		    UED_CTG    = #{UED_CTG}
		
		WHERE UED_SEQ  = #{UED_SEQ}

mapper.delete:
	 
	 	DELETE FROM	TB_TRAN_UNEARNED
	 	WHERE UED_SEQ IN 
	 	
            #{item}

mapper.insert:
INSERT INTO TB_TRAN_SALARY (
SAL_SEQ, SAL_DTL_SEQ, USER_SEQ, SAL_DATE, AMOUNT, PAY_DEDUC, PAY_DEDUC_DTL, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{SAL_SEQ}, #{SAL_DTL_SEQ}, #{USER_SEQ}, #{SAL_DATE}, #{AMOUNT}, #{PAY_DEDUC}, #{PAY_DEDUC_DTL}, #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.select:
	 
	 	SELECT   PAY_DEDUC_DTL,
		         AMOUNT
		   FROM  TB_TRAN_SALARY
		   WHERE SAL_SEQ   = #{SAL_SEQ}
		   AND   PAY_DEDUC = #{PAY_DEDUC}
		  
		ORDER BY SAL_DTL_SEQ ASC


	SELECT  DISTINCT SAL_SEQ, SAL_DATE
     FROM  TB_TRAN_SALARY
     WHERE SAL_SEQ  = #{SAL_SEQ}

 	
 	SELECT  COUNT(*)
	FROM TB_TRAN_SALARY
	WHERE   USER_SEQ = #{USER_SEQ}
	AND     SAL_DATE = #{SAL_DATE}

 
 	SELECT CASE WHEN COUNT(*) > 0 THEN 'TRUE'
		        ELSE 'FALSE'
		        END AS RESULT
     FROM TB_TRAN_YEARLY_ASSET_PLAN
    WHERE STD_YEAR = #{STD_YEAR}
      AND USER_SEQ = #{USER_SEQ}

mapper.insert:
INSERT INTO TB_TRAN_YEARLY_ASSET_PLAN (
ASSET_PLAN_SEQ, USER_SEQ, STD_YEAR, STD_MONTH, MAIN_CTG, SUB_CTG, AMOUNT, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{ASSET_PLAN_SEQ}, #{USER_SEQ}, #{STD_YEAR}, #{STD_MONTH}, #{MAIN_CTG}, #{SUB_CTG}, #{AMOUNT}, #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.delete:
 
 		DELETE FROM TB_TRAN_YEARLY_ASSET_PLAN
		WHERE  USER_SEQ = #{USER_SEQ}
		AND    STD_YEAR = #{STD_YEAR}
