mapper.insert:
INSERT INTO TB_META_USER (
USER_SEQ, USER_ID, USER_PW, USER_NM, USER_NM_EN, USER_EMAIL, USER_ADRS, USER_PHONE, ACT_ST, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{USER_SEQ}, #{USER_ID}, #{USER_PW}, #{USER_NM}, #{USER_NM_EN}, #{USER_EMAIL}, #{USER_ADRS}, #{USER_PHONE}, 'A', #{USER_SEQ}, NOW(), #{USER_SEQ}, NOW()
)

mapper.select:
    
    	SELECT  CASE WHEN COUNT(*) = 1 THEN 'TRUE'
		        ELSE 'FALSE'
				END AS RESULT        
			FROM TB_META_USER 
			WHERE USER_ID = #{USER_ID}
			AND   USER_PW = #{USER_PW}
