mapper.select:
SELECT TMR.ROLE_ID, TMR.PAR_ROLE_ID, (SELECT TMR2.ROLE_NM FROM TB_META_ROLE TMR2 WHERE TMR.PAR_ROLE_ID = TMR2.ROLE_ID ) PAR_ROLE_NM, TMR.ROLE_NM, TMR.ROLE_DC, (SELECT TU.USER_NM FROM TB_META_USER TU WHERE TMR.CREATE_ID = TU.USER_SEQ ) CREATE_NM, TMR.CREATE_DATE, TMR.UPDATE_ID, (SELECT TU.USER_NM FROM TB_META_USER TU WHERE TMR.UPDATE_ID = TU.USER_SEQ ) UPDATE_NM, TMR.UPDATE_DATE
FROM TB_META_ROLE TMR
WHERE TMR.ROLE_ID = #{ROLE_ID}

mapper.insert:
INSERT INTO TB_META_ROLE( ROLE_ID, PAR_ROLE_ID, ROLE_NM, ROLE_DC, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE
) VALUES (
#{ROLE_ID}, #{PAR_ROLE_ID}, #{ROLE_NM}, #{ROLE_DC}, #{CREATE_ID}, NOW(), #{UPDATE_ID}, NOW()
)

mapper.update:
UPDATE TB_META_ROLE
SET PAR_ROLE_ID = #{PAR_ROLE_ID}, ROLE_NM = #{ROLE_NM}, ROLE_DC = #{ROLE_DC}, UPDATE_ID = #{UPDATE_ID}, UPDATE_DATE = NOW()
WHERE ROLE_ID = #{ROLE_ID}

mapper.select:
SELECT ROLE_ID, ROLE_NM FROM TB_META_ROLE
WHERE ROLE_ID != #{ROLE_ID}
ORDER BY ROLE_ID ASC
