mapper.select:
SELECT CODE_CD, CODE_ID, CODE_NM, USE_YN, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE, CODE_ORDR
FROM TB_CMM_CDDTL
WHERE CODE_ID = #{CODE_ID} AND CODE_CD = #{CODE_CD}

mapper.insert:
INSERT INTO TB_CMM_CDDTL (CODE_CD, CODE_ID, CODE_NM, USE_Y, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE, CODE_ORDR
) VALUES (
CODE_CD, CODE_ID, CODE_NM, USE_Y, CREATE_ID, CREATE_DATE, UPDATE_ID, UPDATE_DATE, CODE_ORDR )
